import React, { useEffect, useState } from "react";

export default function PokemonInfo({ searchInput }) {
  //api call with search input

  //if res = null
  //return error message

  //else
  //return data

  // Name
  // Height
  // Weight
  // Species
  // A sprite
  // List of base stats
  // The number of games it has appeared in
  // name: "",
  // height: "",
  // weight: "",
  // species: "",
  // baseStats: "",
  // games: "",
  // sprite: "",
  // name: "",
  // height: "",
  // weight: "",
  // species: "",
  // baseStats: "",
  // games: "",
  // sprite: "",
  const [pokemonInfo, setPokemonInfo] = useState({});
  //   const [invalid, setInvalid] = useState(false);

  useEffect(() => {
    fetch(`https://pokeapi.co/api/v2/pokemon/${searchInput}`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setPokemonInfo({
          name: data.name,
          height: data.height,
          weight: data.weight,
          species: data.species.name,
          baseStats: data.stats,
          games: data.game_indices.length,
          sprite: data.sprite,
        });
        return;
      });
  }, [searchInput]);

  if (searchInput === "") {
    return <div></div>;
  } else if (pokemonInfo === null || pokemonInfo === undefined) {
    return <div>{searchInput} is not a pokemon.</div>;
  } else {
    return <div>name: {pokemonInfo.name}</div>;
  }
}
