import React, { useEffect, useState } from "react";

export default function PokemonInfo({ searchInput }) {
  //api call with search input

  //if res = null
  //return error message

  //else
  //return data

  // Name
  // Height
  // Weight
  // Species
  // A sprite
  // List of base stats
  // The number of games it has appeared in
  // name: "",
  // height: "",
  // weight: "",
  // species: "",
  // baseStats: "",
  // games: "",
  // sprite: "",
  // name: "",
  // height: "",
  // weight: "",
  // species: "",
  // baseStats: "",
  // games: "",
  // sprite: "",
  const [pokemonInfo, setPokemonInfo] = useState({});
  const [baseStats, setBaseStats] = useState([]);

  const [notFound, setNotFound] = useState(false);
  //   const [invalid, setInvalid] = useState(false);

  useEffect(() => {
    fetch(`https://pokeapi.co/api/v2/pokemon/${searchInput}`)
      .then((response) => response.json())
      .then((data) => {
        setNotFound(false);
        console.log(data);
        if (data == {}) {
          setNotFound(true);
        } else {
          setPokemonInfo({
            name: data.name,
            height: data.height,
            weight: data.weight,
            species: data.species.name,
            baseStats: data.stats,
            games: data.game_indices.length,
            sprite: data.sprites,
          });
          setBaseStats(data.stats);
          console.log(baseStats);
        }
      })
      .catch((error) => {
        console.log("failed to fetch");
        setNotFound(true);
      });
  }, [searchInput]);

  if (searchInput === "") {
    return <div></div>;
  } else if (notFound) {
    return <div>{searchInput} is not a pokemon.</div>;
  } else {
    console.log(pokemonInfo.sprite[0]);
    return (
      <div>
        <ul>
          <li>Name: {pokemonInfo.name}</li>
          <li>Height: {pokemonInfo.height}</li>
          <li>Weight: {pokemonInfo.weight}</li>
          <li>Species: {pokemonInfo.species}</li>
          <li>
            {
              //JSON.stringify(baseStats)
              baseStats.map((d) => (
                <li>{d.stat.name + " " + d.base_stat}</li>
              ))
            }
          </li>
          <li>Number of Games: {pokemonInfo.games}</li>
          <li>
            <img src={pokemonInfo.sprite[0]}></img>
          </li>
        </ul>
      </div>
    );
  }
}
