import React, { useEffect, useState } from "react";

export default function PokemonInfo({ searchInput }) {
  //api call with search input

  //if res = null
  //return error message

  //else
  //return data

  // Name
  // Height
  // Weight
  // Species
  // A sprite
  // List of base stats
  // The number of games it has appeared in
  // name: "",
  // height: "",
  // weight: "",
  // species: "",
  // baseStats: "",
  // games: "",
  // sprite: "",

  const [pokemonInfo, setPokemonInfo] = useState({
    name: "",
    height: "",
    weight: "",
    species: "",
    baseStats: "",
    games: "",
    sprite: "",
  });
  //   const [name, setName] = useState("");

  function fetchPokemon() {
    fetch(`https://pokeapi.co/api/v2/pokemon/${searchInput}`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data.name);
        setPokemonInfo({
          name: data.name,
          height: data.height,
          weight: data.weight,
          species: data.species.name,
          baseStats: data.stats,
          games: data.game_indices.length,
          sprite: data.sprite,
        });
        return (
          <div>
            name:{data.name}
            height:{data.height}
          </div>
        );
      });
  }

  useEffect((searchInput) => {
    fetchPokemon();
  }, []);

  if (searchInput === "") {
    return <div></div>;
  }
  return <div>{pokemonInfo.name}</div>;
}
